import React from 'react';
import UserNavbar from "../core/userNavbar"

import PieChart, {
  Series,
  Label,
  Connector,
  Size,
  Export
} from 'devextreme-react/pie-chart';

import { Chart, SeriesTemplate, CommonSeriesSettings, Title } from 'devextreme-react/chart';

import { Loss, Growth } from './helper/data';

class userchart extends React.Component {
  constructor(props) {
    super(props);

    this.pointClickHandler = this.pointClickHandler.bind(this);
    this.legendClickHandler = this.legendClickHandler.bind(this);
  }

  render() {
    return (
        <div>
            <div>
              <UserNavbar />
       <PieChart
        id="pie"
        dataSource={Loss}
        palette="Bright"
        title="Area of Countries"
        onPointClick={this.pointClickHandler}
        onLegendClick={this.legendClickHandler}
      >
        <Series
          argumentField="country"
          valueField="area"
        >
          <Label visible={false}>
            <Connector visible={false} width={1} />
          </Label>
        </Series>

        <Size width={500} />
        <Export enabled={true} />
      </PieChart>
      </div>
       <div>
<Chart
id="chart"
palette="Soft"
dataSource={Growth}>
<CommonSeriesSettings
  argumentField="age"
  valueField="number"
  type="bar"
  ignoreEmptyPoints={true}
/>
<SeriesTemplate nameField="age" />
<Title
  text="Age Breakdown of Facebook Users in the U.S."
  subtitle="as of January 2017"
/>
</Chart>
</div>
</div>
    );
  }

  pointClickHandler(e) {
    this.toggleVisibility(e.target);
  }

  legendClickHandler(e) {
    let arg = e.target;
    let item = e.component.getAllSeries()[0].getPointsByArg(arg)[0];

    this.toggleVisibility(item);
  }

  toggleVisibility(item) {
    item.isVisible() ? item.hide() : item.show();
  }
}

export default userchart;
