import React, { useState } from "react";
import  Base from "../core/Base";
import { Link, Redirect } from "react-router-dom";

import {signin, authenticate, isAuthenticated} from "../auth/helper"

const Signin = () => {

  const [values, setValues] = useState({
    email: "",
    password: "",
    error: "",
    loading: false,
    didRedirect: false
  });

  const { email, password, error, loading, didRedirect } = values

const {user} = isAuthenticated();

const handleChange = name => event => {
  setValues({ ...values, error: false, [name]: event.target.value });
};

const loadingMessage = () => {
  return(
    loading && (
      <div className="alert alert-info">
        <h1>Loading...</h1>
      </div>
    )
  );
  };
const errorMessage = () => {
  return(
    <div className="row">
    <div className="col-md-6 offset-sm-3 text-left">
  <div className="alert alert-danger"
    style={{display: error ? "" : "none"}}>
    {error}
  </div>
  </div>
  </div>
  );
};

const onsubmit = event => {
  event.preventDefault();
  setValues({...values, error: false, loading: true})
  signin({email, password})
  .then(data => {
    if (data.error) {
      setValues({...values, error: data.error, loading: false})
    } else {
      authenticate(data, () => {
        setValues({
          ...values,
          didRedirect: true
        });
      });
    }
  })
  .catch(console.log("signin request failed"));
}

const performRedirect = () => {
  if(didRedirect) {
    if (user && user.role === 1) {
      return <p>redirect to admin</p>
    } else {
      return <p>redirect to user</p>
    }
  }
  if(isAuthenticated()) {
    return <Redirect to = "/" />
  }
}

    const signInForm = () => {
        return (
         <div className="row">
         <div className="col-md-6 offset-sm-3 text-left">
           <form>
               <div className="form-group mt-2">
               <label className="text-light">Email</label>
               <input  onChange={handleChange("password")} value={email} className="form-control" type="email" />
             </div>

             <div className="form-group mt-2">
               <label className="text-light">Password</label>
               <input onChange={handleChange("password")} value={password} className="form-control" type="password" />
             </div>
             <button onClick={onsubmit} className="btn btn-success btn-block form-control mt-2">Submit</button>
           </form>
         </div>
       </div>
     );
   };

    return (
         <Base title= "Sign in page" description="A page for user to signin!">
        {loadingMessage()}
        {errorMessage()}
        {signInForm()}
        {performRedirect()}
    <p className="text-white text-center">{values}</p>
        </Base>
    )
}

export default Signin;
