import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { cartEmpty, loadCart } from './helper/cartHelper';
import { getmeToken, processPayment } from './helper/paymentbhelper';
import { createOrder } from './helper/OrderHelper';
import { isAuthenticated } from '../auth/helper';
import DropIn from "braintree-web-drop-in-react";

const Paymentb = ({ products, setReload = f => f, reload = undefined }) => {

    const [info, setInfo] = useState({
        loading: false,
        success: false,
        clientToken: null,
        error: "",
        instance: {}
    });

    const userId = isAuthenticated() && isAuthenticated().user._id;
    const token = isAuthenticated() && isAuthenticated().token;

    const getToken = (userId, token) => {
        getmeToken(userId, token).then((info) => {
            // console.log("INFORMATION", info);
            if (info && info.error) {
                setInfo({ ...info, error: info.error });
            } else {
                const clientToken = info.clientToken;
                setInfo({ clientToken });
            }
        });
    };
    const showbtdropIn = () => {
        return (
            <div>
                {info.clientToken !== null && products.length > 0 ? (
                    <div>
                        <DropIn
                            options={{ authorization: info.clientToken }}
                            onInstance={instance => (info.instance = instance)}
                        />
                        <button className="btn btn-block btn-success rounded" onClick={onPurchase}>
                            Buy Now
                        </button>
                    </div>
                ) : (
                    <h4>Please <Link to="/signin" className="text-white border-bottom">Login</Link> or <Link to="/" className="text-white border-bottom">add something</Link> to cart.</h4>
                )}
            </div>
        );
    }

    useEffect(() => {
        getToken(userId, token);
    }, []);

    const onPurchase = () => {
        setInfo({...info, loading: true });
        let nonce;
        let getNonce = info.instance.requestPaymentMethod().then(data => {
            nonce = data.nonce;
            const paymentData = {
                paymentMethodNonce: nonce,
                amount: getAmount()
            };
            processPayment(userId, token, paymentData).then(response => {
                setInfo({ ...info, success: response?.success, loading: false });
                console.log("PAYMENT SUCCESSFUL")
                const orderData = {
                    products: products,
                    transaction_id: response.transaction?.id,
                    amount: response.transaction.amount,
                }
                createOrder(userId, token, orderData);
                cartEmpty(() => {
                    console.log("Did we got a crash?")
                });
                setReload(!reload);
            }).catch(error => {
                setInfo({ loading: false, success: false })
                console.log("PAYMENT FAILED")
            });
        });
    }

    const getAmount = () => {
        let amount = 0;
        products.map(p => {
            amount = amount + p.price;
        });
        return amount;
    }

    return (
        <div>
            <h3>Total Amount - ${getAmount()}</h3>
            <br />
            {showbtdropIn()}
        </div>
    );
}

export default Paymentb;
